myApp.controller('bookController',['$http','StuffServices','$routeParams',function($http, StuffServices, $routeParams){
  var main=this;
  this.book={};
  this.bookid=$routeParams.bookid;

  this.getBookDetails=function(){
    StuffServices.detailBook(main.bookid).then(function successCallback(response){
      console.log(main.bookid);
      console.log(response.data);
      main.book=response.data;
      //console.log(main.book);

      },function errorCallback(response){
      alert("Some Error Occured.check console log");
      console.log(response);
    });
    return main.book;
  };
  main.getBookDetails();
}]);
