myApp.controller('characterController', ['$http', 'StuffServices', '$routeParams', function($http,StuffServices, $routeParams) {
  var main = this;
  this.character = {};

  this.characterid = $routeParams.characterid;

  this.getCharacterDetails = function() {
    StuffServices.detailCharacter(main.characterid).then(function successCallback(response) {
        //console.log(response.data);

        main.character = response.data;
        console.log(main.character);

      },
      function errorCallback(response) {
        alert("some error occurred. Check the console.");
        console.log(response);
      });
    return main.character;
  
  };
main.getCharacterDetails();
}]);
