myApp.controller('houseController', ['$http', 'StuffServices', '$routeParams', function($http,StuffServices, $routeParams) {
  var main = this;
  this.house = {};
  this.houseid = $routeParams.houseid;


  this.getHouseDetails = function() {
    StuffServices.detailHouse(main.houseid).then(function successCallback(response) {
        //console.log(response.data);
        main.house = response.data;
        console.log(main.house);

      },
      function errorCallback(response) {
        alert("some error occurred. Check the console.");
        console.log(response);
      });
    return main.house;
  };
this.getHouseDetails();

}]);
