
  myApp.config(['$routeProvider', function($routeProvider) {
      $routeProvider
    .when('/', {
      // location of the template
      templateUrl: 'views/index-view.html',
      // Which controller it should use
      controller: 'mainController',
      // what is the alias of that controller.
      controllerAs:'mystuffs'
    })
    .when('/view-book/:bookid',{
      templateUrl :'views/view-book.html',

      controller  :'bookController',

      controllerAs : 'onebook'
    })
    .when('/view-character/:characterid', {
      templateUrl: 'views/view-character.html',
      controller: 'characterController',
      controllerAs: 'onecharacter'
    })
    .when('/view-house/:houseid', {
      templateUrl: 'views/view-house.html',
      controller: 'houseController',
      controllerAs: 'onehouse'
    })
    .otherwise({
      //redirectTo:'/' //we have a better option
                      template   : '<h1>404 page not found</h1>'
    });
}]);
