myApp.factory('StuffServices',function stuffAPIS($http){

  var baseUrl= "https://www.anapioficeandfire.com/api";
  var baseUrl_1= "https://www.anapioficeandfire.com/api/books/";
  var baseUrl_2= "https://www.anapioficeandfire.com/api/characters/";
  var baseUrl_3= "https://www.anapioficeandfire.com/api/houses/";
  var stuffAPIS={};
  stuffAPIS.getBookStuffs=function(){
      return $http.get(baseUrl + '/books?page=1&pageSize=50');
    }
  stuffAPIS.getCharacter = function() {
      return $http.get(baseUrl + '/characters?page=1&pageSize=50')
    }

  stuffAPIS.getHouse = function() {
      return $http.get(baseUrl + '/houses?page=1&pageSize=50')

    }

 stuffAPIS.detailBook = function(bookid) {
      return $http.get(baseUrl_1 + bookid)

    }
 stuffAPIS.detailCharacter = function(characterid) {
      return $http.get(baseUrl_2 + characterid)

    }
stuffAPIS.detailHouse = function(houseid) {
         return $http.get(baseUrl_3 + houseid)

   }
    return stuffAPIS;
});
